// import { userDataAction } from '../reducers/userStore';
import { Cookies } from 'react-cookie';
import { userDataAction } from '../reducers/userStore';
const cookies = new Cookies();

export const setOAuthAction = (data) => {
  return async (dispatch) => {
    try {
      if (cookies.get('access_token')) {
        const response = await fetch(
          'http://localhost:8008/user/authorization',
          {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              Authorization: cookies.get('access_token'),
            },
            body: '',
          }
        );
        const res = await response.json();
        dispatch(userDataAction.setUserData(res));
      if (response.status >= 500) {
        const response = await fetch('http://localhost:8008/user/updateToken', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ refresh_token: cookies.get('refresh_token') }),
        });
        const res = await response.json();
        if (response.ok) {
          cookies.set('access_token', res.access_token);
          cookies.set('refresh_token', res.refresh_token);
        } else {
          cookies.remove('access_token');
          cookies.remove('refresh_token');
        }
      }
    }
    } catch (error) {
      console.error('hi-808: ', error);
    }
  };
};
