import React from 'react';
import { useSelector } from 'react-redux';

import './settingForm.css';

const SettingForm = () => {
  const {setting: {theme, color}} = useSelector((state) => state.userStore.user);

  const handleSubmit = (event) => {
    const localTheme = localStorage.getItem('theme');
    event.preventDefault();
    const formData = Object.fromEntries(new FormData(event.target).entries());
    console.log({ color: formData.color, theme: localTheme });
    console.log();
    
  };

  return (
    <form onSubmit={handleSubmit} className="SettingForm">
      <div className="SettingBox">
        <input className="colorInput" type={'color'} name={'color'} />
      </div>
      <button className="sendButton" type="submit">
        Save
      </button>
    </form>
  );
};

export default SettingForm;
