import { userDataAction } from '../reducers/userStore';

const RegExpEmail = new RegExp('[a-zA-Z0-9]@.');
const RegExpGmail = new RegExp(/^[A-Z0-9._%+-]+@gmail.com$/i)
const RegExpPassword = new RegExp("^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&*])(?=.{8,})");

export const setUserAction = (data) => {
  return async (dispatch) => {
    try {
      if (Object.keys(data).length) {
        let errorList = [];
        // if (data.email.trim() === '') {
        //   errorList.push({ errorType: 'errorText', message: `Email ' '` });
        // }
        if (!RegExpEmail.test(data.email)) {
          errorList.push({ errorType: 'errorText', message: `incorrect email` });
        }
        if (R (data.password)) {
          errorList.push({ errorType: 'errorText', message: `Password ' '` });
        }
        dispatch(userDataAction.setLoginError(errorList));
      }
    } catch (error) {
      console.error('hi-808: ', error);
      return error;
    }
  };
};
