import { createSlice } from '@reduxjs/toolkit';

const mediaSave = createSlice({
  name: 'mediaSave',
  initialState: {
    media: [],
    isDownload: false
  },
  reducers: {
    setMedia: (state, action) => {
        return {...state, media: action.payload, isDownload: true}
    },
    default: (state) => {
      return state;
    },
  },
});
export const mediaStoreAction = mediaSave.actions;

export default mediaSave.reducer;
