import { createSlice } from '@reduxjs/toolkit';

const localTheme = localStorage.getItem('theme');
const themeDefault = localTheme === 'darkTheme' ? 'darkTheme' : 'lightTheme';

const userStore = createSlice({
  name: 'userStore',
  initialState: {
    user: {
      name: '',
      setting: {
        theme: themeDefault,
        backgraungColor: '#1A1A1A',
        color: '#1889C8',
      },
    },
    errors: [],
  },
  reducers: {
    setLoginError: (state, action) => {
      return { ...state, errors: action.payload };
    },
    setUserData: (state, action) => {
      return { ...state, user: action.payload, errors: [] };
    },
    removeUserData: (state) => {
      return { user: { name: '' } };
    },
    changeTheme: (state) => {
      const theme = state.theme === 'lightTheme' ? 'darkTheme' : 'lightTheme';
      localStorage.setItem('theme', theme);
      
      return { ...state, user: {setting : {theme}} };
    },
  },
});

export const userStoreAction = userStore.actions;

export default userStore.reducer;
