import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import './searchEngine.css';
import { setMediaAction } from '../../store/actions/mediaAction';
import { useDispatch } from 'react-redux';
import searchSVG from '../../image/search.svg'

const SearchEngine = (props) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [searchText, setSearchText] = useState('');
  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = Object.fromEntries(new FormData(event.target).entries());
    console.log(formData);
    // if (formData.optionValue != 'default') {
    //     console.log(formData.optionValue);
    // }
    if (formData.searchValue) {
      setSearchText(formData.searchValue);
      console.log(searchText);
      setMediaAction({ name: searchText }, 'search')(dispatch);
      navigate(`/search?name=${searchText}`);
    }
  };
  const handleSearchText = (e) => {
    setSearchText(e.target.value);
  };

  return (
    <section className="SearchEngine">
      <form onSubmit={handleSubmit}>
        <input
          className="searchText"
          name="searchValue"
          value={searchText}
          onChange={handleSearchText}
          placeholder="search name"
          type="text"
        />
        {/* <select defaultValue={'default'} name='optionValue'>
                    <option value={'default'}>default</option>
                    <option value={2}>2</option>
                    <option value={3}>3</option>
                </select> */}
        {/* <input className="button" type="submit" value={''} /> */}
        <img className="button" onClick={handleSubmit} src={searchSVG} alt="" />
      </form>
    </section>
  );
};

export default SearchEngine;
