import { createSlice } from '@reduxjs/toolkit';

const localTheme = localStorage.getItem('theme');
const themeDefault = localTheme === 'darkTheme' ? 'darkTheme' : 'lightTheme';

const mediaSave = createSlice({
  name: 'mediaSave',
  initialState: {
    media: []
  },
  reducers: {
    refreshMovies: (state, action) => {
        const media = action.payload;
        return {...state, media}
    },
    // changeColor: (state, action) => {
    //   const {color} = action.payload
    //   return {...state, color}
    // },
    // updataSetting: (state, action) => {
    //   const {color} = action.payload
    //   const theme = action.payload.theme === 'darkTheme' ? 'darkTheme' : 'lightTheme';
    //   return {...state, color, theme}
    // },
    restart: (state) => {
      return { ...state };
    },
  },
});
export const mediaSaveAction = mediaSave.actions;

export default mediaSave.reducer;
