import { createSlice } from '@reduxjs/toolkit';
import API_MOVIES from '../../API/moviesServise';

const mediaStore = createSlice({
  name: 'mediaStore',
  initialState: {
    media: [],
    isDownload: false
  },
  reducers: {
    setMedia: (state, action) => {
        return {...state, media: action.payload, isDownload: true}
    },
    setMediaApi: (state) => {
      const media = API_MOVIES.foundMovies({name: ''})
      console.log('store', media);
      return {...state, media}
    },
