import { createSlice } from '@reduxjs/toolkit';
import API_MOVIES from '../../API/moviesServise';

const mediaStore = createSlice({
  name: 'mediaStore',
  initialState: {
    media: [],
    isDownload: false
  },
  reducers: {
    setMedia: (state, action) => {
        return {...state, media: action.payload, isDownload: true}
    },
    setMediaApi: (state) => {
      const data = API_MOVIES.foundMovies()
      return {...state}
    },
    default: (state) => {
      return state;
    },
  },
});
export const mediaStoreAction = mediaStore.actions;

export default mediaStore.reducer;
