import { createSlice } from '@reduxjs/toolkit';

const mediaSave = createSlice({
  name: 'mediaSave',
  initialState: {
    media: [],
    isDownload: false
  },
  reducers: {
    refreshMovies: (state, action) => {
        return {...state, media: action.payload, i}
    },
    restart: (state) => {
      return { ...state };
    },
  },
});
export const mediaSaveAction = mediaSave.actions;

export default mediaSave.reducer;
