import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useSearchParams } from 'react-router-dom';
import { setMediaAction } from '../../store/actions/mediaAction';
import MovieInfo from '../../components/movieInfo/movieInfo';
// import VideoPlayer from '../../components/videoPlayer/videoPlayer';

const About = () => {
  const [searchParams] = useSearchParams();
  const movieId = searchParams.get('id');

  const dispatch = useDispatch();
  const dataRaw = useSelector((state) => state.mediaStore);

  const [data, setData] = useState({});

  const updateMedia = async () => {
    if (dataRaw.media._id || dataRaw.searchMedia._id) {
      setData(dataRaw);
    }
    
    if (dataRaw.length) {
      const [dataOne] = await dataRaw.filter((e) => e._id === movieId);
      setData(dataOne);
    }
    if (!data._id && !dataRaw._id && !dataRaw.length || !dataRaw.searchMedia) {
      const dataRefresh = await setMediaAction({ _id: movieId }, 'search')(dispatch);
      setData(dataRefresh);
    }

    // if (dataRaw.length) {
    //   const [dataOne] = await dataRaw.filter((e) => e._id === movieId);
    //   setData(dataOne);
    // }
    // if (!data._id && !dataRaw._id && !dataRaw.length || !dataRaw.searchMedia) {
    //   const dataRefresh = await setMediaAction({ _id: movieId }, 'search')(dispatch);
    //   setData(dataRefresh);
    // }
  };

  useEffect(() => {
    if (!data._id) {
      updateMedia();
    }
  });

  return (
    <React.Fragment>
      <MovieInfo data={data} />
      {/* <VideoPlayer /> */}
    </React.Fragment>
  );
};

export default About;
