import { createSlice } from '@reduxjs/toolkit';

const localTheme = localStorage.getItem('theme');
const themeDefault = localTheme === 'darkTheme' ? 'darkTheme' : 'lightTheme';

const userStore = createSlice({
  name: 'userStore',
  initialState: {
    user: {
      name: '',
      setting: {
        theme: themeDefault,
        color: '#1889C8',
      },
    },
    errors: [],
    isLogin: false
  },
  reducers: {
    setLoginError: (state, action) => {
      return { ...state, errors: action.payload };
    },
    setUserData: (state, action) => {
      // localStorage.setItem('theme', action.payload.setting.theme);
      return { ...state, user: {...action.payload, setting: {...action.payload.setting, theme: themeDefault}}, errors: [] };
    },
    removeUserData: (state) => {
      return { user: { name: '' } };
    },
    changeTheme: (state) => {
      const theme = state.user.setting.theme === 'lightTheme' ? 'darkTheme' : 'lightTheme';
      localStorage.setItem('theme', theme);
      return {...state, user: { ...state.user, setting: { ...state.user.setting, theme: theme }}};
    },
  },
});

export const userStoreAction = userStore.actions;

export default userStore.reducer;
