import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { settingStyleAction } from '../../store/settingStyle'

import './menuSetting.css';

const MenuSetting = (props) => {
  const textTheme = useSelector((state) => state.themeStyle);
  
  console.log(textTheme.theme);
  const dispatch = useDispatch();
  return (
    <div className="close" onClick={() => props.show(false)}>
      <div className="menuSetting">
        <p>Setting</p>
        <p onClick={()=> dispatch(settingStyleAction.changeTheme(textTheme.text.theme))}>{textTheme.text.theme}</p>
      </div>
    </div>
  );
};

export default MenuSetting;
