import React, { useEffect, useState } from 'react';
import UserInput from '../userInput/userInput';

import './userForm.css';

import EmailImage from '../../image/emailSvg.svg';
import PesswordIgame from '../../image/passwordSvg.svg';

const UserFormI = (props) => {
  const [formData, setFormData] = useState({});
  const [isLogin, setIsLogin] = useState(true);

  useEffect(() => {
    if (Object.keys(formData)) {
      props.data(formData)
    }
    console.log(1, formData, !!Object.keys(formData));
  }, [formData]);

  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = Object.fromEntries(new FormData(event.target).entries());
    setFormData(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <button
        onClick={() => setIsLogin(!isLogin)}
        className="buttonChange"
        type="button"
      ></button>
      <div className="boxInput">
        {!isLogin && (
          <label>
            <img src={EmailImage} />
            <UserInput type={'text'} name={'name'} placeholder={'name'} />
          </label>
        )}
        <label>
          <img src={EmailImage} />
          <UserInput type={'text'} name={'email'} placeholder={'email'} />
        </label>
        <label>
          <img src={PesswordIgame} />
          <UserInput
            type={'password'}
            name={'password'}
            placeholder={'password'}
          />
        </label>
        {!isLogin && (
          <label>
            <img src={PesswordIgame} />
            <UserInput
              type={'password'}
              name={'passwordTwo'}
              placeholder={'passwordTwo'}
            />
          </label>
        )}
      </div>
      <button className='sendButton' type="submit">login</button>
    </form>
  );
};

export default UserFormI;
