import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useSearchParams } from 'react-router-dom';
import { setMediaAction } from '../../store/actions/mediaAction';
import MovieInfo from '../../components/movieInfo/movieInfo';
// import VideoPlayer from '../../components/videoPlayer/videoPlayer';

const About = () => {
  const [searchParams] = useSearchParams();
  const movieId = searchParams.get('id');

  const dispatch = useDispatch();
  const dataRaw = useSelector((state) => state.mediaStore);

  const [isFound, setIsFound] = useState(false);
  const [data, setData] = useState({});

  const updateMedia = async () => {
    if (dataRaw.media._id || dataRaw.searchMedia._id) {
      setData(dataRaw);
    }

    if (dataRaw.media.length) {
      const [dataOne] = await dataRaw.media.filter((e) => e._id === movieId);
      setData(dataOne);
    }
    if (
      (!data._id && !dataRaw.media._id && !dataRaw.media.length) ||
      !dataRaw.media.searchMedia
    ) {
      const dataRefresh = await setMediaAction(
        { _id: movieId },
        'getMedia'
      )(dispatch);
      setData(dataRefresh);
    }

    if (dataRaw.searchMedia.length) {
      const [dataOne] = await dataRaw.searchMedia.filter(
        (e) => e._id === movieId
      );
      setData(dataOne);
    }
    if (
      (!data._id && !dataRaw.searchMedia._id && !dataRaw.searchMedia.length) ||
      !dataRaw.media
    ) {
      const dataRefresh = await setMediaAction(
        { _id: movieId },
        'search'
      )(dispatch);
      setData(dataRefresh);
    }
  };

  useEffect(() => {
      if (!data._id && isFound) {
        updateMedia();
        setIsFound()
      }
  });

  return (
    <React.Fragment>
      <MovieInfo data={data} />
      {/* <VideoPlayer /> */}
    </React.Fragment>
  );
};

export default About;
