import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useSearchParams } from 'react-router-dom';
import { setMediaAction } from '../../store/actions/mediaAction';
import { mediaStoreAction } from '../../store/reducers/mediaStore';

const About = () => {
  const dispatch = useDispatch();
  const dataRaw = useSelector((state) => state.mediaStore.media);
  
  const [data, setData] = useState({});
  const [searchParams] = useSearchParams();
  const movieId = searchParams.get('id');


  const updateMedia = async () => {
    if (dataRaw.length) {
      const [dataOne] = await dataRaw.filter((e) => e._id === movieId);
      setData(dataOne);
    }
    if (!data) {
      const d = await setMediaAction({ _id: movieId })(dispatch);
      // setData(d);
      console.log(1, d);
    } else {
      console.log('test',data, dataRaw.length);
      console.log(2, data);
    }
  };

  useEffect(() => {
    if (!data || !data.length) {
      updateMedia();
    }
    console.log(3);
  });

  return (
    <React.Fragment>
      <p>rtgerg</p>
      <p>{data.name}</p>
      {data.image && <img className="filmImage" src={data.image} alt="" />}
    </React.Fragment>
  );
};

export default About;
