import { createSlice } from '@reduxjs/toolkit';

const mediaStore = createSlice({
  name: 'mediaStore',
  initialState: {
    media: [],
    isDownload: false
  },
  reducers: {
    setMedia: (state, action) => {
        return {...state, media: action.payload, isDownload: true}
    },
    localSearch: (state, action) => {
      const media = action
      return {...state}
    },
    default: (state) => {
      return state;
    },
  },
});
export const mediaStoreAction = mediaStore.actions;

export default mediaStore.reducer;
