import React, { useState } from 'react';
import { createPortal } from 'react-dom';

import './header.css';

import logo from '../image/808.jpg';
import Navigator from '../items/navigator';
import MenuSetting from '../items/menuSetting/menuSetting';

const Header = () => {
  const [showMenu, setShowMenu] = useState(false)
  // document.body.style.overflowY = 'auto';
  if (showMenu) {
    document.body.style.overflowY = 'hidde';
  } else {
    document.body.style.overflowY = 'auto';
  }
  return (
    <header>
      <img className="mainLogo" onClick={()=>setShowMenu(true)} src={logo} alt="logo" width={'50px'} />
      {showMenu && createPortal(<MenuSetting  show={setShowMenu} />, document.body) }
      <Navigator />
    </header>
  );
};

export default Header;
