import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useSearchParams } from 'react-router-dom';
import { setMediaAction } from '../../store/actions/mediaAction';
import { mediaStoreAction } from '../../store/reducers/mediaStore';

const About = () => {
  const dispatch = useDispatch();
//   const [data, setData] = useState({});
  const [searchParams] = useSearchParams();
  const movieId = searchParams.get('id');

  const data = useSelector((state) => state.mediaStore.aboutMedia);
  console.log(data, !data || !data.length );

  const updateMedia = ()=>{
    if (!data || !data.length) {
      const d = setMediaAction({_id: movieId})(dispatch);
      console.log(1, d);
    } else {
      console.log(2, data);
    }
  }

  useEffect(() => {
    updateMedia()
    console.log(3);
  });

  return (
    <React.Fragment>
      <p>rtgerg</p>
      <p>{data.name}</p>
      {data.image && <img className="filmImage" src={data.image} alt="" />}
    </React.Fragment>
  );
};

export default About;
