import React, { useState } from 'react';
import { useDispatch } from 'react-redux';

import SimpleInput from '../../items/simpleInput/simpleInput';
import Instars from '../../items/stars'

import './movieForm.css';
// import { Link } from 'react-router-dom';

const boy =
  'https://m.media-amazon.com/images/M/MV5BYTY2ZjYyNGUtZGVkZS00MDNhLWIwMjMtZDk4MmQ5ZWI0NTY4XkEyXkFqcGdeQXVyMTY3MDE5MDY1._V1_QL75_UY281_CR18,0,190,281_.jpg';

const MovieForm = (props) => {
  const dispatch = useDispatch();

  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = Object.fromEntries(new FormData(event.target).entries());
    // setUserAction(formData)(dispatch);
    console.log(formData);
  };

  return (
    <React.Fragment>
      <form onSubmit={handleSubmit} className="movieInfo movieForm">
        <img className="image" src={boy} alt="" />
        <div className="infoGrid">
          <input
            className="name"
            type={'text'}
            name={'name'}
            placeholder={'name'}
            value={'props.data.name'}
          />

          <div className="starsBox"></div>

          <div className="infoBox">
            <div>
              <p className="infoName">genre</p>
              <input
                className="infoData"
                type={'text'}
                name={'name'}
                placeholder={'name'}
                value={'props.data.name'}
              />
            </div>
            <div>
              <p className="infoName">ageRating</p>
              <p className="infoData">+props.data.ageRating</p>
            </div>
            <div>
              <p className="infoName">rating</p>
              <p className="infoData">Math.roundprops.data.rating</p>
            </div>
            <div>
              <p className="infoName">year</p>
              <p className="infoData">props.data.year</p>
            </div>
          </div>

          <div className="description">
            <h2 className="infoName">Description:</h2>
            <div>
              <p className="infoData">props.data.description</p>
            </div>
          </div>
          <button className="sendButton" type="submit">
            Create and save
          </button>
        </div>
      </form>
      {/* <form onSubmit={handleSubmit} className="movieForm">
        <div className="boxInput">
          <label>
            <input type={'text'} name={'name'} placeholder={'name'} />
          </label>
        </div>
        <button className="sendButton" type="submit">
          Create and save
        </button>
      </form> */}
    </React.Fragment>
  );
};

export default MovieForm;
