import React, { useState } from 'react';
import UserInput from '../userInput/userInput';

import './userForm.css';

import EmailImage from '../../image/emailSvg.svg';
import PesswordIgame from '../../image/passwordSvg.svg';

const UserForm = (props) => {
  const dispatch = us
  const [isLogin, setIsLogin] = useState(true);

  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = Object.fromEntries(new FormData(event.target).entries());
    props.data(formData);
    props.formType(isLogin);
    setUserAction(userData, formType)(dispatch);
  };

  return (
    <form onSubmit={handleSubmit}>
      <button
        onClick={() => setIsLogin(!isLogin)}
        className="buttonChange"
        type="button"
      ></button>
      <div className="boxInput">
        {!isLogin && (
          <label>
            <img src={EmailImage} alt=''/>
            <UserInput type={'text'} name={'name'} placeholder={'name'} />
          </label>
        )}
        <label>
          <img src={EmailImage} alt=''/>
          <UserInput type={'text'} name={'email'} placeholder={'email'} />
        </label>
        <label>
          <img src={PesswordIgame} alt=''/>
          <UserInput
            type={'password'}
            name={'password'}
            placeholder={'password'}
          />
        </label>
        {!isLogin && (
          <label>
            <img src={PesswordIgame} alt=''/>
            <UserInput
              type={'password'}
              name={'passwordTwo'}
              placeholder={'passwordTwo'}
            />
          </label>
        )}
      </div>
      <button className='sendButton' type="submit">login</button>
    </form>
  );
};

export default UserForm;
