import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import API_MOVIES from '../../API/moviesServise';

import CollectionCards from '../../components/collectionCards/collectionCards';
import Header from '../../components/header';
import mediaSave from '../../store/mediaSave';

const Movies = () => {
  // const dispatch = useDispatch();
  // const [moviesData, setMoviesData] = useState([]);
  // const temp = useSelector(state => state.mediaSave);

  // const getMovies = async () => {
  //   if (!moviesData[0] && !temp[0]) {

  //     setMoviesData(await API_MOVIES.foundMovies({ name: '' }));
  //     dispatch(mediaSave.refreshMovies({media: moviesData}))
  //   }
  // };
  // getMovies();
  const [isLoading, setIsLoading] = useState(false);
  const [mediaData, setMediaData] = useState([]);
  const { media: response } = useSelector((state) => state.mediaSave);
  console.log(3, response);
  useEffect(() => {
    const server = async () => {
      setIsLoading(true);
      if (!response[0]) {
        const res = await API_MOVIES.foundMovies({ name: '' });
        setMediaData(res);
        console.log(2);
      } else {
        if (response[0]) {
          setMediaData(response);
          console.log(response);
        }
      }
      setIsLoading(false);
    };
    server();
  }, []);

  return (
    <React.Fragment>
      {mediaData[0] && <CollectionCards data={mediaData} />}
      {isLoading && <p>not found</p>}
    </React.Fragment>
  );
};

export default Movies;
