import React, { useEffect, useState } from 'react';
import UserInput from '../userInput/userInput';

import './userForm.css';

import EmailImage from '../../image/emailSvg.svg';
import PesswordIgame from '../../image/passwordSvg.svg'

const UserForm = () => {
  const [formData, setFormData] = useState({});
  const [isLogin, setIsLogin] = useState(true);

  useEffect(() => {
    console.log(1, formData);
  }, [formData]);

  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = Object.fromEntries(new FormData(event.target).entries());
    setFormData(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <button onClick={()=>setIsLogin(!isLogin)} type='button'></button>
      {!isLogin && <label>
        <img src={PesswordIgame} />
        <UserInput
          type={'text'}
          name={'name'}
          placeholder={'name'}
        />
      </label>}
      <label>
        <img src={EmailImage} />
        <UserInput type={'text'} name={'email'} placeholder={'email'} />
      </label>
      <label>
        <img src={PesswordIgame} />
        <UserInput
          type={'password'}
          name={'password'}
          placeholder={'password'}
        />
      </label>
      {!isLogin && <label>
        <img src={PesswordIgame} />
        <UserInput
          type={'password'}
          name={'passwordTwo'}
          placeholder={'passwordTwo'}
        />
      </label>}
      <button type="submit">login</button>
    </form>
  );
};

export default UserForm;
