import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { settingStyleAction } from '../../store/settingStyle'

import './menuSetting.css';
const theme = useSelector(state => state.themeStyle.theme);

const MenuSetting = (props) => {
  const dispatch = useDispatch();
  const theme = useSelector((state) => state.themeStyle.theme);
  const textTheme = theme === 'darkTheme' ? 'dark theme' : 'light theme';
  
  return (
    <div className="close" onClick={() => props.show(false)}>
      <div className="menuSetting">
        <p>Setting</p>
        <p onClick={()=> dispatch(settingStyleAction.changeTheme())}>{textTheme}</p>
        <p>Sign out</p>
      </div>
    </div>
  );
};

export default MenuSetting;
