import React, { useEffect, useState } from 'react';

import './movieInfo.css';
// import InputStar from '../../items/stars/stars';
import { setMediaAction } from '../../store/actions/mediaAction';
import { useDispatch, useSelector } from 'react-redux';

const MovieInfo = (props) => {
  const dispatch = useDispatch();
  const userIsLogin = useSelector((state) => state.userStore.user.name);

  useEffect(() => {
    if (userIsLogin) {
      setDisabled(false);
    }
  }, [userIsLogin]);

  const reting = (elem) => {
    if (elem.target.value) {
      setStar(Math.round(elem.target.value));
      setMediaAction(
        { filmId: props.data._id, points: elem.target.value },
        'rating'
      )(dispatch);
    }
  };

  return (
    <section className="movieInfo">
      <img src={props.data.image} alt="" />
      <div className="infoGrid">
        <h1>{props.data.name}</h1>

        <div className="starsBox">
          {/* <form onClick={reting} name="formReting" className={'rating'}>
            {stars}
          </form> */}
        </div>

        <div className="infoBox">
          <div>
            <p className="infoName">genre</p>
            <p className="infoData">{props.data.genre}</p>
          </div>
          <div>
            <p className="infoName">ageRating</p>
            <p className="infoData">+{props.data.ageRating}</p>
          </div>
          <div>
            <p className="infoName">rating</p>
            <p className="infoData">{star}</p>
          </div>
          <div>
            <p className="infoName">year</p>
            <p className="infoData">{props.data.year}</p>
          </div>
        </div>

        <div className="description">
          <h2 className="infoName">Description:</h2>
          <div>
            <p className="infoData">{props.data.description}</p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default MovieInfo;
